import React, { useState } from "react";
import { useNavigate } from "react-router";

export default function Create() {
  const [form, setForm] = useState({
    ItemName: "",
    BuyPrice: "",
    Quantity: "",
    SellPrice: "",
    Tax: "",
    IncleTax: "",
    Profit: "",
  });
  const navigate = useNavigate();

  // These methods will update the state properties.
  function updateForm(value) {
    return setForm((prev) => {
      return { ...prev, ...value };
    });
  }

  // This function will handle the submission.
  async function onSubmit(e) {
    e.preventDefault();

    // When a post request is sent to the create url, we'll add a new record to the database.
    const newFlip = { ...form };

    await fetch("http://localhost:5000/record/add", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newFlip),
    })
    .catch(error => {
      window.alert(error);
      return;
    });

    setForm({ItemName: "", BuyPrice: "", Quantity: "", SellPrice: "", Tax: "",IncleTax: "", Profit: ""});
    navigate("/");
  }

  // This following section will display the form that takes the input from the user.
  return (
    <div className="container">
    <div style={{"width" : "35%"}}>
      <h3>Enter your flip</h3><br></br>
      <form onSubmit={onSubmit}>
        <div className="form-group">
          <label htmlFor="ItemName">Item Name</label>
          <input
            type="text"
            className="form-control"
            id="ItemName"
            value={form.ItemName}
            onChange={(e) => updateForm({ ItemName: e.target.value })}
          />
        </div>
        <div className="form-group">
          <label htmlFor="BuyPrice">Buy Price</label>
          <input
            type="number"
            className="form-control"
            id="BuyPrice"
            value={form.BuyPrice}
            onChange={(e) => updateForm({ BuyPrice: e.target.value })}
          />
        </div>
        <div className="form-group">
          <label htmlFor="Quantity">Quantity</label>
          <input
            type="number"
            className="form-control"
            id="Quantity"
            value={form.Quantity}
            onChange={(e) => updateForm({ Quantity: e.target.value })}
          />
        </div>
        <div className="form-group">
          <label htmlFor="SellPrice">Sell Price</label>
          <input
            type="number"
            className="form-control"
            id="SellPrice"
            value={form.SellPrice}
            onChange={(e) => updateForm({ sellPrice: e.target.value })}
          />
        </div>
        <div class="flex-containerCalcs">
          <div class="flex-itemsCalcs">
                    <text>Tax: </text><br></br>
          </div>
          <div class="flex-itemsCalcs">
            <text>Approx. Profit (incl. Tax): </text><br></br>
          </div>
          <div class="flex-itemsCalcs">
            <text>Total Profit: </text><br></br>
          </div>
        </div>
        <div className="form-group">
          <input
            type="submit"
            value="Log flip"
            className="btn btn-primary"
          />
        </div>
      </form>
    </div>
    </div>
  );
}
