import React, { useState } from "react";
import { useNavigate } from "react-router";

export default function Create() {
  const [form, setForm] = useState({
    ItemName: "",
    BuyPrice: "",
    Quantity: "",
    SellPrice: "",
    Tax: "",
    IncleTax: "",
    Profit: "",
  });
  const navigate = useNavigate();

  // These methods will update the state properties.
  function updateForm(value) {
    return setForm((prev) => {
      return { ...prev, ...value };
    });
  }



  // This function will handle the submission.
  async function onSubmit(e) {
    e.preventDefault();

    // When a post request is sent to the create url, we'll add a new record to the database.
    const newFlip = { ...form };

    await fetch("http://localhost:5000/record/add", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newFlip),
    })
    .catch(error => {
      window.alert(error);
      return;
    });

    setForm({ItemName: "", BuyPrice: "", Quantity: "", SellPrice: "", Tax: "",IncleTax: "", Profit: ""});
    navigate("/");
  }

  // This following section will display the form that takes the input from the user.
  return (

    function Calcs() {
      var tax,incleTax,profit 
      tax = (form.BuyPrice - form.SellPrice) * 0.1;
      incleTax = tax - form.SellPrice;
      profit = incleTax - form.BuyPrice;
      document.getElementsByClassName("tax")[0].innerHTML = "Tax: -" + tax;
      if (incleTax < 0) {
        document.getElementsByClassName("incleTax")[0].innerHTML = "Approx. Profit (incl. Tax):  -" + incleTax;
      }else
      document.getElementsByClassName("incleTax")[0].innerHTML = "Approx. Profit (incl. Tax): " + incleTax;
      if (profit < 0) {
        document.getElementsByClassName("profit")[0].innerHTML = "Profit: -" + profit;
      }else
      document.getElementsByClassName("profit")[0].innerHTML = "Profit: " + profit;
    },
    document.getElementsByClassName("ItemName").onkeyup = function() {Calcs()},
    document.getElementByClassName("BuyPrice").onkeyup = function() {Calcs()},
    document.getElementByClassName("Quantity").onkeyup = function() {Calcs()},
    document.getElementByClassName("SellPrice").onkeyup = function() {Calcs()},
    <div style={{"width" : "35%", "margin": "auto"}}>
      <h3 style={{"margin-left": "20%"}}>Enter your flip</h3><br></br>
      <form onSubmit={onSubmit}>
        <div className="form-group">
          <label htmlFor="ItemName">Item Name</label>
          <input
            type="text"
            className="form-control ItemName"
            id="ItemName"
            value={form.ItemName}
            onKeyUp="Calcs()"
            onChange={(e) => updateForm({ ItemName: e.target.value })}
          />
        </div>
        <div className="form-group">
          <label htmlFor="BuyPrice">Buy Price</label>
          <input
            type="number"
            className="form-control BuyPrice"
            id="BuyPrice"
            value={form.BuyPrice}
            onChange={(e) => updateForm({ BuyPrice: e.target.value })}
          />
        </div>
        <div className="form-group">
          <label htmlFor="Quantity">Quantity</label>
          <input
            type="number"
            className="form-control Quantity"
            id="Quantity"
            value={form.Quantity}
            onChange={(e) => updateForm({ Quantity: e.target.value })}
          />
        </div>
        <div className="form-group">
          <label htmlFor="SellPrice">Sell Price</label>
          <input
            type="number"
            className="form-control SellPrice"
            id="SellPrice"
            value={form.SellPrice}
            onChange={(e) => updateForm({ SellPrice: e.target.value })}
          />
        </div>
        <div class="flex-containerCalcs">
          <div class="flex-itemsCalcs">
            <text className="Tax">Tax: </text>
          </div>
          <div class="flex-itemsCalcs"><br></br>
            <text className="incleTax">Approx. Profit (incl. Tax): </text>
          </div>
          <div class="flex-itemsCalcs"><br></br>
            <text className="profit">Total Profit: </text>
          </div>
        </div>
        <div className="form-group"><br></br>
          <input
            type="submit"
            value="Log flip"
            className="btn btn-primary"
          />
        </div>
      </form>
    </div>
  );
}
