import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import './assets/styles.css';

const Record = (props) => (
  <tr>
    <td>{props.record.ItemName}</td>
    <td>ㅤㅤ{props.record.BuyPrice}</td>
    <td>ㅤㅤ{props.record.Quantity}</td>
    <td>ㅤㅤ{props.record.SellPrice}</td>
    <td>{props.record.Tax}</td>
    <td>ㅤㅤㅤㅤㅤㅤㅤ{props.record.IncleTax}</td>
    <td>ㅤㅤㅤ{props.record.Profit}</td>
    <td>
      <Link className="btn btn-link" to={`/edit/${props.record._id}`}>Edit</Link> |
      <button className="btn btn-link"
        onClick={() => {
          props.deleteRecord(props.record._id);
        }}
      >
        Delete
      </button>
    </td>
  </tr>
);

export default function RecordList() {
  const [records, setRecords] = useState([]);

  // This method fetches the records from the database.
  useEffect(() => {
    async function getRecords() {
      const response = await fetch(`http://localhost:5000/record/`);

      if (!response.ok) {
        const message = `An error occured: ${response.statusText}`;
        window.alert(message);
        return;
      }

      const records = await response.json();
      setRecords(records);
    }

    getRecords();

    return; 
  }, [records.length]);

  // This method will delete a record
  async function deleteRecord(id) {
    await fetch(`http://localhost:5000/${id}`, {
      method: "DELETE"
    });

    const newRecords = records.filter((el) => el._id !== id);
    setRecords(newRecords);
  }

  // This method will map out the records on the table
  function recordList() {
    return records.map((record) => {
      return (
        <Record
          record={record}
          deleteRecord={() => deleteRecord(record._id)}
          key={record._id}
        />
      );
    });
  }
//get props.record.Profit from the database and sums all the profits
  function sumProfit() {
    let sum = 0;
    for (let i = 0; i < records.length; i++) {
      sum += records[i].Profit;
    }
    return sum;
  }

  // This following section will display the table with the records of individuals.
  return (
    <div>
      <div style={{ "display": "inline-block" }}>
      <h3 style={{ "margin-left": "44.5%" }}>Flip Log</h3> <h3 style={{ "margin-left": "38.5%" }}>Total Profits: {sumProfit()}</h3>
      </div>
      <table className="table table-striped" style={{ marginTop: 20 }}>
        <thead>
          <tr>
            <th>Item Name</th>
            <th>Buy Price</th>
            <th>Quantity</th>
            <th>Sell Price</th>
            <th>ㅤTax</th>
            <th>Profit (incl. Tax)</th>
            <th>Total Profit</th>
            <th>ㅤㅤㅤㅤActions</th>
          </tr>
        </thead>
        <tbody>{recordList()}</tbody>
      </table>
    </div>
  );
}
